    private void menuInputActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_menuInputActionPerformed
        // TODO add your handling code here:
        if (configDB.isLogin) {
            TabelHafidz tabelhafidz = new TabelHafidz();
            tabelhafidz.setVisible(true);
        } else {
            JOptionPane.showMessageDialog(this, "Silahkan Login Terlebih Dahulu!",
                    "Warning",JOptionPane.WARNING_MESSAGE);
            new Login().setVisible(true);
            this.dispose();
        }
    }//GEN-LAST:event_menuInputActionPerformed

